    private void jSpinner1StateChanged(javax.swing.event.ChangeEvent evt) {
        int quantity = (Integer) jSpinner1.getValue();
        double final_price = quantity*3.5;
        this.jLabel12.setText(Double.toString(final_price));
        double total_price=Double.parseDouble(this.jLabel15.getText());
        total_price+=final_price;
        this.jLabel15.setText(Double.toString(total_price));
    }

