    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {

        String feedback = JOptionPane.showInputDialog(getContentPane(), "Please enter your feedback");
        String item1 = "Set A";
        double price1=3.5;
        int quantity1 = (Integer)jSpinner1.getValue();
        MenuItem menuItem1 = new MenuItem(item1,price1,quantity1);


        String item2 = "Set B";
        double price2=9.5;
        int quantity2 = (Integer)jSpinner2.getValue();
        MenuItem menuItem2 = new MenuItem(item2,price2,quantity2);

        String item3 = "Set C";
        double price3=19.5;
        int quantity3 = (Integer)jSpinner3.getValue();
        MenuItem menuItem3 = new MenuItem(item3,price3,quantity3);

        ReceiptList receiptList = new ReceiptList(user.getUsername());

        receiptList.addItem(menuItem1);
        receiptList.addItem(menuItem2);
        receiptList.addItem(menuItem3);

        receiptList.setOrdercount(quantity1+quantity2+quantity3);
        receiptList.setFeedback(feedback);
        receiptList.write_to_file();
        dispose();
    }


